document.addEventListener("DOMContentLoaded",()=>{const progress=document.querySelector(".reading-progress");if(!progress)return;let ticking=false;window.addEventListener("scroll",()=>{if(!ticking){requestAnimationFrame(()=>{const docHeight=document.documentElement.scrollHeight-window.innerHeight;const scrollPercent=(window.scrollY/docHeight)*100;progress.style.setProperty("--scroll-percent",`${scrollPercent}%`);ticking=false;});ticking=true;}});});document.addEventListener("DOMContentLoaded",()=>{const indicator=document.querySelector(".scroll-indicator");if(!indicator)return;const updateScrollIndicator=()=>{const docHeight=document.documentElement.scrollHeight;const viewHeight=window.innerHeight;const scrolled=window.scrollY;const scrollPercent=(scrolled/(docHeight-viewHeight))*100;indicator.style.setProperty("--scroll-percent",`${scrollPercent}%`);indicator.style.height=`${scrollPercent}%`;};window.addEventListener("scroll",updateScrollIndicator);updateScrollIndicator();});document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".md-clipboard").forEach((button)=>{button.addEventListener("click",function(e){e.preventDefault();const codeBlock=this.closest(".highlight");const oldFlash=codeBlock.querySelector(".copy-flash");if(oldFlash){oldFlash.remove();}
const flash=document.createElement("div");flash.className="copy-flash";codeBlock.appendChild(flash);flash.addEventListener("animationend",()=>{flash.remove();});});});});function getContent(){return`一闪一闪亮晶晶，不及<span class="love-highlight">小亮</span><span class="love-heart">♥</span>照我心。To Li Liang`;}
document.addEventListener("DOMContentLoaded",()=>{const loveDiv=document.querySelector(".md-footer-love");if(!loveDiv)return;loveDiv.addEventListener("mouseenter",()=>{const heart=loveDiv.querySelector(".love-heart");if(heart){heart.style.animationDuration="0.8s";}});loveDiv.addEventListener("mouseleave",()=>{const heart=loveDiv.querySelector(".love-heart");if(heart){heart.style.animationDuration="1.5s";}});});