function adjustBackToTopPosition(){const footer=document.querySelector('.md-footer');if(footer){const footerHeight=footer.offsetHeight;document.documentElement.style.setProperty('--footer-height',footerHeight+'px');}}
document.addEventListener('DOMContentLoaded',function(){adjustBackToTopPosition();window.addEventListener('resize',adjustBackToTopPosition);const observer=new MutationObserver(adjustBackToTopPosition);observer.observe(document.body,{childList:true,subtree:true,attributes:true,attributeFilter:['class','style']});});