document.addEventListener('DOMContentLoaded',function(){const tabsList=document.querySelector('.md-tabs__list');if(!tabsList)return;const tabItems=tabsList.querySelectorAll('.md-tabs__item');if(tabItems.length===0)return;function updateOverlayPosition(targetItem){if(!targetItem)return;const rect=targetItem.getBoundingClientRect();const listRect=tabsList.getBoundingClientRect();const left=rect.left-listRect.left;const width=rect.width;let styleElement=document.getElementById('navigation-overlay-styles');if(!styleElement){styleElement=document.createElement('style');styleElement.id='navigation-overlay-styles';document.head.appendChild(styleElement);}
styleElement.textContent=`
            .md-tabs__list::before {
                left: ${left}px !important;
                width: ${width}px !important;
            }
        `;}
function showOverlayWithAnimation(){tabsList.classList.add('show-hover-overlay');}
function getItemIndex(item){return Array.from(tabItems).indexOf(item);}
function triggerMoveAnimation(fromItem,toItem){if(isAnimating)return;const fromIndex=getItemIndex(fromItem);const toIndex=getItemIndex(toItem);tabsList.classList.remove('animating-left','animating-right');isAnimating=true;if(toIndex>fromIndex){tabsList.classList.add('animating-right');}else{tabsList.classList.add('animating-left');}
setTimeout(()=>{tabsList.classList.remove('animating-left','animating-right');isAnimating=false;},600);}
function hideOverlay(){tabsList.classList.remove('show-hover-overlay','animating-left','animating-right');isAnimating=false;}
let isAnimating=false;let currentHoverItem=null;tabItems.forEach(item=>{item.addEventListener('mouseenter',function(){const activeItem=tabsList.querySelector('.md-tabs__item--active');if(this!==activeItem){if(currentHoverItem!==this&&!isAnimating){updateOverlayPosition(this);showOverlayWithAnimation();currentHoverItem=this;}else if(currentHoverItem!==this&&currentHoverItem!==null){updateOverlayPosition(this);triggerMoveAnimation(currentHoverItem,this);currentHoverItem=this;}}});item.addEventListener('mouseleave',function(){hideOverlay();currentHoverItem=null;});});tabItems.forEach(item=>{const link=item.querySelector('.md-tabs__link');if(link){link.addEventListener('click',function(){hideOverlay();});}});let resizeTimeout;window.addEventListener('resize',function(){clearTimeout(resizeTimeout);resizeTimeout=setTimeout(()=>{hideOverlay();},100);});});