document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".md-clipboard").forEach((button)=>{button.addEventListener("click",function(e){e.preventDefault();const codeBlock=this.closest(".highlight");const oldFlash=codeBlock.querySelector(".copy-flash");if(oldFlash){oldFlash.remove();}
const flash=document.createElement("div");flash.className="copy-flash";codeBlock.appendChild(flash);flash.addEventListener("animationend",()=>{flash.remove();});});});});function getContent(){return`一闪一闪亮晶晶，不及<span class="love-highlight">小亮</span><span class="love-heart">♥</span>照我心。To Li Liang`;}
document.addEventListener("DOMContentLoaded",()=>{const loveDiv=document.querySelector(".md-footer-love");if(!loveDiv)return;loveDiv.addEventListener("mouseenter",()=>{const heart=loveDiv.querySelector(".love-heart");if(heart){heart.style.animationDuration="0.8s";}});loveDiv.addEventListener("mouseleave",()=>{const heart=loveDiv.querySelector(".love-heart");if(heart){heart.style.animationDuration="1.5s";}});});